mapper.select:
select

  distinct

'true' as QUERYID,

from forum_topics_viewer




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

mapper.insert:
insert into forum_topics_viewer (id, forum_topics_viewer_code, is_disable, 
  is_delete, tenant_code, org_code, 
  org_level, create_time, modify_time, 
  remark, forum_topics_code, viewer_code
  )
values (#{id,jdbcType=BIGINT}, #{forumTopicsViewerCode,jdbcType=VARCHAR}, #{isDisable,jdbcType=TINYINT}, 
  #{isDelete,jdbcType=TINYINT}, #{tenantCode,jdbcType=VARCHAR}, #{orgCode,jdbcType=VARCHAR}, 
  #{orgLevel,jdbcType=TINYINT}, #{createTime,jdbcType=BIGINT}, #{modifyTime,jdbcType=BIGINT}, 
  #{remark,jdbcType=VARCHAR}, #{forumTopicsCode,jdbcType=VARCHAR}, #{viewerCode,jdbcType=VARCHAR}
  )

mapper.update:
  	UPDATE forum_topics
		SET view_count = view_count + 1 ,
			modify_time = #{modifyTime}
		WHERE is_disable = 0
			AND is_hide = 0
			AND is_delete = 0
			AND forum_topics_code = #{topicCode}
			AND tenant_code = #{tenantCode}
