mapper.select:
select

  distinct

'true' as QUERYID,

from activity_comment_reply




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

	 SELECT 
   	  	acr.id AS id,
		acr.code AS code,
		acr.comment_code AS commentCode,
		acr.activity_code AS activityCode,
		acr.replier_code AS replierCode,
		acr.questioner_code AS questionerCode,
		acr.content AS content,
		acr.create_time AS createTime,
		acr.praise_count AS praiseCount,
		ap.admirer_code AS admirerCode
 	  FROM 
		(SELECT * FROM activity_comment_reply aca 
			WHERE aca.id <= (
					SELECT acb.id FROM activity_comment_reply acb
						WHERE acb.activity_code = #{activityCode}
							AND acb.comment_code = #{commentCode}
							AND acb.code = #{code}
							AND acb.is_delete = 0
							AND acb.is_disable = 0
							AND acb.tenant_code = #{tenantCode}
		)
 	) acr
       LEFT JOIN activity_praise ap
      ON ap.info_code = acr.code
       	 AND ap.info_type = #{infoType}
      	 AND ap.admirer_code =  #{admirerCode}
      	 AND ap.is_delete = 0
      	 AND ap.is_disable = 0
      	 AND ap.tenant_code = #{tenantCode}
       WHERE  acr.is_disable = 0
    	 AND acr.is_delete = 0
    	 AND acr.activity_code =  #{activityCode}
    	 AND acr.comment_code = #{commentCode}
    	 AND acr.tenant_code = #{tenantCode}

    	     AND acr.id <#{id} 

    	 ORDER BY acr.id DESC
      LIMIT #{limitStart},#{limitEnd}

  	 SELECT COUNT(1)
 	 FROM 
		(SELECT * FROM activity_comment_reply aca 
			WHERE aca.id <= (
					SELECT acb.id FROM activity_comment_reply acb
						WHERE acb.activity_code = #{activityCode}
							AND acb.comment_code = #{commentCode}
							AND acb.code = #{code}
							AND acb.is_delete = 0
							AND acb.is_disable = 0
							AND acb.tenant_code = #{tenantCode}
		)
 	) acr
       LEFT JOIN activity_praise ap
      ON ap.info_code = acr.code
       	 AND ap.info_type = #{infoType}
      	 AND ap.admirer_code =  #{admirerCode}
      	 AND ap.is_delete = 0
      	 AND ap.is_disable = 0
      	 AND ap.tenant_code = #{tenantCode}
       WHERE  acr.is_disable = 0
    	 AND acr.is_delete = 0
    	 AND acr.activity_code =  #{activityCode}
    	 AND acr.comment_code = #{commentCode}
    	 AND acr.tenant_code = #{tenantCode}

    	     AND acr.id <#{id}

SELECT acr.id AS id, acr.code AS code, acr.comment_code AS commentCode, acr.activity_code AS activityCode, acr.replier_code AS replierCode, acr.questioner_code AS questionerCode, acr.content AS content, acr.create_time AS createTime, acr.praise_count AS praiseCount, ap.admirer_code AS admirerCode FROM activity_comment_reply acr LEFT JOIN activity_praise ap ON ap.info_code = acr.code AND ap.info_type = #{infoType} AND ap.admirer_code = #{admirerCode} AND ap.is_delete = 0 AND ap.is_disable = 0 and ap.tenant_code = #{tenantCode} WHERE acr.is_disable = 0 AND acr.is_delete = 0 AND acr.activity_code = #{activityCode} and acr.comment_code = #{commentCode} and acr.tenant_code = #{tenantCode}
AND acr.id <#{id}
ORDER BY acr.id DESC LIMIT #{limitStart},#{limitEnd}

    SELECT 
		count(1)
	 FROM activity_comment_reply acr
       WHERE  acr.is_disable = 0
    	 AND acr.is_delete = 0
    	 AND acr.activity_code = #{activityCode}
    	 AND acr.comment_code = #{commentCode}
    	 AND acr.tenant_code = #{tenantCode}

    	    AND acr.id <#{id}

mapper.update:
  	UPDATE activity_comment_reply
	SET praise_count = praise_count ${express},
		modify_time = #{modifyTime}
	WHERE code = #{code}
