mapper.select:
select

  distinct

'true' as QUERYID,

from honor_praise




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

mapper.delete:
    DELETE FROM honor_praise
    WHERE admirer_code = #{admirerCode}
		AND info_code = #{infoCode}
		AND info_type = #{infoType}
		AND is_delete = 0
		AND is_disable = 0
		AND tenant_code = #{tenantCode}
